INSERT INTO `lep_mod_wysiwyg` (`section_id`, `page_id`, `content`, `text`) VALUES (1, 1, '<h1>Welcome to LEPTON v2.0 Black Cat Edition!</h1>\r\n<p>&nbsp;</p>\r\n<p>This is the LEPTON v2.0 default page for English language. Just edit the contents in the backend to fit your needs.</p>\r\n<p>&nbsp;</p>\r\n<p>If you don\'t need an English welcome page, you may <a href="#del">delete this page</a> in the backend or change it\'s language setting and link to the German page.</p>\r\n<p>&nbsp;</p>\r\n<h2>How do I edit a page?</h2>\r\n<p>&nbsp;</p>\r\n<p>In the page tree, just click on the page you wish to edit.</p>\r\n<p>&nbsp;</p>\r\n<h2><a name="#del">How do I delete a page?</a></h2>\r\n<p>&nbsp;</p>\r\n<p>In the page tree, hover the page you wish to delete to see the config icon.</p>\r\n<p>&nbsp;</p>\r\n<p><img alt="Hover page to see the icon" src="https://raw.github.com/webbird/LEPTON_2_BlackCat/master/screenshots/2013-01-03_154135.jpg" style="width: 250px; height: 157px;" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Click on that icon to open the configuration dialog.</p>\r\n<p>&nbsp;</p>\r\n<p><img alt="" src="https://raw.github.com/webbird/LEPTON_2_BlackCat/master/screenshots/2013-01-03_154203.jpg" style="width: 489px; height: 494px;" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Click on [Remove page].</p>\r\n<p>&nbsp;</p>\r\n<h1><strong>Learn more</strong></h1>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><a href="http://blackcat-cms.org" target="_blank">LEPTON v2.0 Black Cat Edition Homepage</a></p>\r\n<p><a href="https://github.com/webbird/LEPTON_2_BlackCat">GitHub</a></p>\r\n<p>Forum</p>\r\n<p>Addons repository</p>\r\n<p>End user documentation</p>\r\n<p><a href="https://github.com/webbird/LEPTON_2_BlackCat/wiki/API-Reference">API reference</a></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n', 'Welcome to LEPTON v2.0 Black Cat Edition!\r\n&nbsp;\r\nThis is the LEPTON v2.0 default page for English language. Just edit the contents in the backend to fit your needs.\r\n&nbsp;\r\nIf you don\'t need an English welcome page, you may delete this page in the backend or change it\'s language setting and link to the German page.\r\n&nbsp;\r\nHow do I edit a page?\r\n&nbsp;\r\nIn the page tree, just click on the page you wish to edit.\r\n&nbsp;\r\nHow do I delete a page?\r\n&nbsp;\r\nIn the page tree, hover the page you wish to delete to see the config icon.\r\n&nbsp;\r\n\r\n&nbsp;\r\n&nbsp;\r\nClick on that icon to open the configuration dialog.\r\n&nbsp;\r\n\r\n&nbsp;\r\n&nbsp;\r\nClick on [Remove page].\r\n&nbsp;\r\nLearn more\r\n&nbsp;\r\n&nbsp;\r\nLEPTON v2.0 Black Cat Edition Homepage\r\nGitHub\r\nForum\r\nAddons repository\r\nEnd user documentation\r\nAPI reference\r\n&nbsp;\r\n&nbsp;\r\n');
INSERT INTO `lep_mod_wysiwyg` (`section_id`, `page_id`, `content`, `text`) VALUES (2, 2, '<h1>Willkommen bei LEPTON v2.0 Black Cat Edition!</h1>\r\n<p>Dies ist die LEPTON v2.0 Standardseite fÃ¼r die Deutsche Sprache. Bearbeiten Sie einfach den Inhalt der Seite im Backend. Wenn Sie keine deutschsprachige Heimatseite benÃ¶tigen, kÃ¶nnen Sie diese Seite auch lÃ¶schen oder die Spracheinstellungen und die VerknÃ¼pfung zur englischsprachigen Seite Ã¤ndern.</p>\r\n<p>&nbsp;</p>\r\n<p>--- ToDo: Howto ---</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Weitere Informationen:</strong></p>\r\n<p><a href="http://blackcat-cms.org" target="_blank">LEPTON v2.0 Black Cat Edition Homepage</a></p>\r\n<p><a href="https://github.com/webbird/LEPTON_2_BlackCat">GitHub (englisch)</a></p>\r\n<p>Forum</p>\r\n<p>Addons repository</p>\r\n<p>End user documentation</p>\r\n<p><a href="https://github.com/webbird/LEPTON_2_BlackCat/wiki/API-Reference">API Referenz (englisch)</a></p>\r\n', 'Willkommen bei LEPTON v2.0 Black Cat Edition!\r\nDies ist die LEPTON v2.0 Standardseite f&uuml;r die Deutsche Sprache. Bearbeiten Sie einfach den Inhalt der Seite im Backend. Wenn Sie keine deutschsprachige Heimatseite ben&ouml;tigen, k&ouml;nnen Sie diese Seite auch l&ouml;schen oder die Spracheinstellungen und die Verkn&uuml;pfung zur englischsprachigen Seite &auml;ndern.\r\n&nbsp;\r\n--- ToDo: Howto ---\r\n&nbsp;\r\nWeitere Informationen:\r\nLEPTON v2.0 Black Cat Edition Homepage\r\nGitHub (englisch)\r\nForum\r\nAddons repository\r\nEnd user documentation\r\nAPI Referenz (englisch)\r\n');
