INSERT INTO `cat_mod_wysiwyg` (`section_id`, `page_id`, `content`, `text`) VALUES (1, 1, '<h1>Welcome to Black Cat CMS!</h1>\r\n<p>&nbsp;</p>\r\n<p>This is the Black Cat CMS default page for English language. Just edit the contents in the backend to fit your needs.</p>\r\n<p>&nbsp;</p>\r\n<p>If you don\'t need an English welcome page, you may <a href="#del">delete this page</a> in the backend or change it\'s language setting and link to the German page.</p>\r\n<p>&nbsp;</p>\r\n<h2>How do I edit a page?</h2>\r\n<p>&nbsp;</p>\r\n<p>In the page tree, just click on the page you wish to edit.</p>\r\n<p>&nbsp;</p>\r\n<h2><a name="#del">How do I delete a page?</a></h2>\r\n<p>&nbsp;</p>\r\n<p>In the page tree, hover the page you wish to delete to see the config icon.</p>\r\n<p>&nbsp;</p>\r\n<p><img alt="Hover page to see the icon" src="https://raw.github.com/webbird/LEPTON_2_BlackCat/master/screenshots/2013-01-03_154135.jpg" style="width: 250px; height: 157px;" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Click on that icon to open the configuration dialog.</p>\r\n<p>&nbsp;</p>\r\n<p><img alt="" src="https://raw.github.com/webbird/LEPTON_2_BlackCat/master/screenshots/2013-01-03_154203.jpg" style="width: 489px; height: 494px;" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Click on [Remove page].</p>\r\n<p>&nbsp;</p>\r\n<h1><strong>Learn more</strong></h1>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><a href="http://blackcat-cms.org" target="_blank">Black Cat CMS Homepage</a></p>\r\n<p><a href="https://github.com/webbird/LEPTON_2_BlackCat">GitHub</a></p>\r\n<p>Forum</p>\r\n<p>Addons repository</p>\r\n<p>End user documentation</p>\r\n<p><a href="https://github.com/webbird/LEPTON_2_BlackCat/wiki/API-Reference">API reference</a></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n', 'Welcome to Black Cat CMS!\r\n&nbsp;\r\nThis is the Black Cat CMS default page for English language. Just edit the contents in the backend to fit your needs.\r\n&nbsp;\r\nIf you don\'t need an English welcome page, you may delete this page in the backend or change it\'s language setting and link to the German page.\r\n&nbsp;\r\nHow do I edit a page?\r\n&nbsp;\r\nIn the page tree, just click on the page you wish to edit.\r\n&nbsp;\r\nHow do I delete a page?\r\n&nbsp;\r\nIn the page tree, hover the page you wish to delete to see the config icon.\r\n&nbsp;\r\n\r\n&nbsp;\r\n&nbsp;\r\nClick on that icon to open the configuration dialog.\r\n&nbsp;\r\n\r\n&nbsp;\r\n&nbsp;\r\nClick on [Remove page].\r\n&nbsp;\r\nLearn more\r\n&nbsp;\r\n&nbsp;\r\nBlack Cat CMS Homepage\r\nGitHub\r\nForum\r\nAddons repository\r\nEnd user documentation\r\nAPI reference\r\n&nbsp;\r\n&nbsp;\r\n');
INSERT INTO `cat_mod_wysiwyg` (`section_id`, `page_id`, `content`, `text`) VALUES (2, 2, '<h1>Willkommen bei Black Cat CMS!</h1>\r\n\r\n<p>Dies ist die Black Cat CMS Standardseite f&uuml;r die Deutsche Sprache. Bearbeiten Sie einfach den Inhalt der Seite im Backend. Wenn Sie keine deutschsprachige Heimatseite ben&ouml;tigen, k&ouml;nnen Sie diese Seite auch l&ouml;schen oder die Spracheinstellungen und die Verkn&uuml;pfung zur englischsprachigen Seite &auml;ndern.</p>\r\n\r\n<p>--- ToDo: Howto ---</p>\r\n\r\n<p><strong>Weitere Informationen:</strong></p>\r\n\r\n<p><a href="http://blackcat-cms.org" target="_blank">Black Cat CMS Homepage</a></p>\r\n\r\n<p><a href="https://github.com/webbird/LEPTON_2_BlackCat">GitHub (englisch)</a></p>\r\n\r\n<p>Forum</p>\r\n\r\n<p>Addons repository</p>\r\n\r\n<p>End user documentation</p>\r\n\r\n<p><a href="https://github.com/webbird/LEPTON_2_BlackCat/wiki/API-Reference">API Referenz (englisch)</a></p>\r\n', 'Willkommen bei Black Cat CMS!\r\n\r\nDies ist die Black Cat CMS Standardseite f&uuml;r die Deutsche Sprache. Bearbeiten Sie einfach den Inhalt der Seite im Backend. Wenn Sie keine deutschsprachige Heimatseite ben&ouml;tigen, k&ouml;nnen Sie diese Seite auch l&ouml;schen oder die Spracheinstellungen und die Verkn&uuml;pfung zur englischsprachigen Seite &auml;ndern.\r\n\r\n--- ToDo: Howto ---\r\n\r\nWeitere Informationen:\r\n\r\nBlack Cat CMS Homepage\r\n\r\nGitHub (englisch)\r\n\r\nForum\r\n\r\nAddons repository\r\n\r\nEnd user documentation\r\n\r\nAPI Referenz (englisch)\r\n');
INSERT INTO `cat_mod_wysiwyg` (`section_id`, `page_id`, `content`, `text`) VALUES (3, 3, '<div style="color:#FF0000;background-color:#FFA07A;font-weight:900;text-align:center;padding:5px;">Sorry, this site is down for maintenance. Please check back later.</div>\r\n\r\n<div style="color:#FF0000;background-color:#FFA07A;font-weight:900;text-align:center;padding:5px;">Entschuldigung, diese Seite ist wegen Wartungsarbeiten derzeit nicht verf&uuml;gbar. Bitte besuchen Sie uns sp&auml;ter noch einmal.</div>\r\n', 'Sorry, this site is down for maintenance. Please check back later.\r\n\r\nEntschuldigung, diese Seite ist wegen Wartungsarbeiten derzeit nicht verf&uuml;gbar. Bitte besuchen Sie uns sp&auml;ter noch einmal.\r\n');
